import { SETTODOS } from "../types/rootTypes";
import axios from "axios";

const todos = [];

const reducer = (state = todos, action) => {
	let data = [];
	if (action.type === SETTODOS) {
		axios.get("/todos").then((res) => {
			console.log(res.data);
			data = [...res.data];
			console.log(data);
		});

		return data;
	} else {
		return state;
	}
};

export default reducer;
