import React, { useState, useEffect } from "react";
import axios from "axios";
import styled from "styled-components";

const Input = styled.input`
	color: white;
	background-image: linear-gradient(to right, #14213d, #023e8a);
	border-radius: 20px;
	border: none;
	padding: 10px;
	width: 60%;
	font-size: 15px;
	font-weight: 500;
	font-family: "Kanit", sans-serif;
	transition: background-image 0.4 ease;

	&:focus {
		outline: none;
		background-image: linear-gradient(to right, #023e8a, #14213d);
	}
	::placeholder {
		color: rgba(255, 255, 255, 0.7);
		font-size: 15px;
		font-weight: 500;
		font-family: "Kanit", sans-serif;
	}
`;
const Button = styled.button`
	border-radius: 10px;
	background-color: #55a630;
	width: 20%;
	padding: 5px;
	color: white;
	font-size: 15px;
	font-weight: 500;
	font-family: "Kanit", sans-serif;
	border: 1px solid black;

	&:hover {
		background-color: #80b918;
	}

	&:focus {
		outline: none;
		border: 1px solid grey;
	}
`;

const Div = styled.div`
	display: flex;
`;

function Form() {
	const [customers, setCustomers] = useState([]);
	const [inputValue, setinputValue] = useState("");
	useEffect(() => {
		axios("/api").then((res) => {
			setCustomers(res.data);
		});
	}, []);

	const submitHandler = (e) => {
		e.preventDefault();
		setinputValue("");
	};
	const changeHandler = (e) => {
		setinputValue(e.target.value);
	};
	return (
		<Div className="form">
			{customers.map((customer) => {
				return (
					<p>
						{customer.name} , {customer.age} , the id is {customer.id}
					</p>
				);
			})}
			<form onSubmit={submitHandler}>
				<Input
					value={inputValue}
					onChange={changeHandler}
					className="form__input"
					placeholder="ajouter une autre action ...  "
				/>
				<Button type="submit" className="form__button">
					Add
				</Button>
			</form>
		</Div>
	);
}

export default Form;
