import { SETTODOS } from "../types/rootTypes";
import axios from "axios";

const todos = [];

const reducer = (state = todos, action) => {
	if (action.type === SETTODOS) {
		axios.get("/todos").then((res) => {
			state = [...state, ...res.data];
		});
		return state;
	} else {
		return state;
	}
};

export default reducer;
