import { SETTODOS } from "../types/rootTypes";

const reducer = (state = [], action) => {
	if (action.type === SETTODOS) {
		state = [...state, ...action.data];
		console.log(state);
		return state;
	} else {
		return state;
	}
};

export default reducer;
