import { INCREASE, DECREASE, GETTODOS } from "../types/rootTypes";

const todos = {
	id: null,
	text: "",
	isdone: false,
};

const reducer = (state = todos, action) => {
	if (action.type === INCREASE) {
		return { count: state.count + 1 };
	} else if (action.type === DECREASE) {
		return { count: state.count - 1 };
	} else if (action.type === GETTODOS) {
	} else {
		return state;
	}
};

export default reducer;
