import React, { useEffect } from "react";
import avatar from "../assets/ayoub.jpeg";
import "../assets/NavBar.css";
import { Link } from "react-router-dom";
function NavBar() {
	var links = [
		{
			name: "Todo",
			active: true,
		},
		{
			name: "Chat",
			active: false,
		},
		{
			name: "About",
			active: false,
		},
		{
			name: "ContactUs",
			active: false,
		},
	];

	const changeColor = (name) => {
		console.log(name);
		links[name] = true;
	};
	return (
		<nav className="nav">
			<h3 className="nav__title">
				ToDo <span>List</span>
			</h3>

			<ul className="nav__list">
				{links.map((link) => {
					return (
						<li id={`/${link.name}`} className={link.active && "nav__active"}>
							<Link
								onClick={changeColor.bind(this, link.name)}
								to={`/${link.name}`}
							>
								{link.name}
							</Link>
						</li>
					);
				})}
			</ul>
			<img src={avatar} width="40px" />
		</nav>
	);
}

export default NavBar;
