import React, { useEffect } from "react";
import avatar from "../assets/ayoub.jpeg";
import "../assets/NavBar.css";
import { Link } from "react-router-dom";
function NavBar() {
	useEffect(() => {
		var id = window.location.pathname;
		console.log(id);
		if (id === "/") {
			console.log(document.getElementById(`${id}`));
			document.getElementById("/Todo").style.backgroundColor = "teal";
		} else {
			console.log(document.getElementById(`${id}`));
			document.getElementById(`${id}`).style.backgroundColor = "teal";
		}
	}, []);

	const links = [
		{
			name: "Todo",
			state: true,
		},
		{
			name: "Chat",
			state: true,
		},
		{
			name: "About",
			state: true,
		},
		{
			name: "ContactUs",
			state: true,
		},
	];
	return (
		<nav className="nav">
			<h3 className="nav__title">
				ToDo <span>List</span>
			</h3>

			<ul className="nav__list">
				{links.map((link) => {
					return (
						<li id={`/${link.name}`} className={link.active && "nav__active"}>
							<Link to={`/${link.name}`}>{link.name}</Link>
						</li>
					);
				})}
			</ul>
			<img src={avatar} width="40px" />
		</nav>
	);
}

export default NavBar;
