import { SETTODOS } from "../types/rootTypes";
import axios from "axios";

const todos = [];

const reducer = (state = todos, action) => {
	let data = [];
	if (action.type === SETTODOS) {
		axios.get("/todos").then((res) => {
			console.log("******************************************");
			data = [
				...todos,
				{ id: res.data.id, text: res.data.text, isdone: res.data.isdone },
			];
		});
		console.log(data);
		return data;
	} else {
		return state;
	}
};

export default reducer;
