import React, { useState, useEffect } from "react";
import avatar from "../assets/ayoub.jpeg";
import "../assets/NavBar.css";
import { Link } from "react-router-dom";
function NavBar() {
	const [path, setPath] = useState("");
	useEffect(() => {
		setPath(window.location.pathname);
		console.log(path);
		if (path === "/") {
			console.log(document.getElementById(`${path}`));
			document.getElementById("/Todo").style.backgroundColor = "teal";
		} else {
			console.log(document.getElementById(`${path}`));
			document.getElementById(`${path}`).style.backgroundColor = "teal";
		}
	}, [path]);

	const links = ["Todo", "Chat", "About", "ContactUs"];
	return (
		<nav className="nav">
			<h3 className="nav__title">
				ToDo <span>List</span>
			</h3>

			<ul className="nav__list">
				{links.map((link) => {
					return (
						<li id={`/${link}`}>
							<Link to={`/${link}`}>{link}</Link>
						</li>
					);
				})}
			</ul>
			<img src={avatar} width="40px" />
		</nav>
	);
}

export default NavBar;
