import React from "react";
import styled from "styled-components";
import { AiOutlineDelete } from "react-icons/ai";
import { FaRegEdit } from "react-icons/fa";

const Div = styled.div`
	background-image: linear-gradient(to bottom, #0096c7, #0077b6, #14213d);
	border-radius: 30px;
	padding: 20px;
	.todos__ligne {
		display: flex;
		color: white;
	}
	.Todos__icons {
		justify-content: center;
	}
`;

const Li = styled.li`
	color: white;
	list-style: none;
	margin-bottom: 10px;
`;

const todos = [
	{
		id: 1,
		text: "the first one",
	},
	{
		id: 2,
		text: "the second one",
	},
	{
		id: 3,
		text: "the third one",
	},
];
function Todos() {
	return (
		<Div>
			<ul>
				{todos.map(({ id, text }) => {
					return (
						<div className="todos__ligne">
							<Li key={id}>{text}</Li>

							<AiOutlineDelete className="todos__delete" />
							<FaRegEdit className="todos__edit" />
						</div>
					);
				})}
			</ul>
		</Div>
	);
}

export default Todos;
