import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { AiOutlineDelete } from "react-icons/ai";
import { FaRegEdit } from "react-icons/fa";
import { IconContext } from "react-icons";
import axios from "axios";

const Div = styled.div`
	background-image: linear-gradient(to bottom, #0096c7, #0077b6, #14213d);
	border-radius: 30px;
	padding: 10px;
	.todos__ligne {
		cursor: pointer;
		display: flex;
		color: white;
		padding: 5px 20px;
		transition: background-color 100ms ease;
		border-radius: 20px;
		background-color: rgba(72, 202, 228, 0);
		&:hover {
			border-radius: 20px;
			background-color: rgba(72, 202, 228, 0.4);
		}
	}

	.react-icons {
		justify-content: center;
		transition: background-color 100ms ease;
		&:hover {
			background-color: rgba(0, 0, 0, 0.3);
		}

		border-radius: 20px;
		padding: 5px;
		margin-right: 5px;
		font-size: 20px;
	}
`;

const Li = styled.li`
	color: white;
	list-style: none;
	margin-bottom: 10px;
`;

function Todos() {
	const [todos, setTodos] = useState([]);
	useEffect(() => {
		axios("/todos").then((res) => {
			setTodos(res.data);
		});
	}, []);

	const onClickHandler = () => {
		console.log("message");
	};
	return (
		<Div>
			<ul>
				{todos.map(({ id, text }) => {
					return (
						<div className="todos__ligne">
							<div style={{ width: "70%" }}>
								<Li key={id}>{text}</Li>
							</div>
							<div style={{ width: "20%", justifyContent: "center" }}>
								<IconContext.Provider
									value={{ color: "red", className: "react-icons" }}
								>
									<AiOutlineDelete size="20" onClick={onClickHandler} />
								</IconContext.Provider>
								<IconContext.Provider
									value={{ color: "blue", className: "react-icons" }}
								>
									<FaRegEdit size="20" />
								</IconContext.Provider>
							</div>
						</div>
					);
				})}
			</ul>
		</Div>
	);
}

export default Todos;
